<div class="language-java line-numbers-mode" data-ext="java" data-title="java"><pre v-pre class="language-java"><code><span class="token keyword">package</span> <span class="token namespace">gui<span class="token punctuation">.</span>events</span><span class="token punctuation">;</span>

<span class="token keyword">import</span> <span class="token import"><span class="token namespace">enemies<span class="token punctuation">.</span></span><span class="token class-name">Enemy</span></span><span class="token punctuation">;</span>
<span class="token keyword">import</span> <span class="token import"><span class="token namespace">gui<span class="token punctuation">.</span>exceptions<span class="token punctuation">.</span></span><span class="token class-name">EnemyDeadException</span></span><span class="token punctuation">;</span>
<span class="token keyword">import</span> <span class="token import"><span class="token namespace">gui<span class="token punctuation">.</span>exceptions<span class="token punctuation">.</span></span><span class="token class-name">PlayerDeadException</span></span><span class="token punctuation">;</span>
<span class="token keyword">import</span> <span class="token import"><span class="token namespace">gui<span class="token punctuation">.</span>windows<span class="token punctuation">.</span></span><span class="token class-name">GameWindow</span></span><span class="token punctuation">;</span>
