<span class="token keyword">import</span> <span class="token import"><span class="token namespace">player<span class="token punctuation">.</span></span><span class="token class-name">Player</span></span><span class="token punctuation">;</span>
<span class="token keyword">import</span> <span class="token import"><span class="token namespace">util<span class="token punctuation">.</span>enemies<span class="token punctuation">.</span></span><span class="token class-name">EnemyFactory</span></span><span class="token punctuation">;</span>

<span class="token keyword">import</span> <span class="token import"><span class="token namespace">java<span class="token punctuation">.</span>awt<span class="token punctuation">.</span>event<span class="token punctuation">.</span></span><span class="token class-name">ActionEvent</span></span><span class="token punctuation">;</span>
<span class="token keyword">import</span> <span class="token import"><span class="token namespace">java<span class="token punctuation">.</span>awt<span class="token punctuation">.</span>event<span class="token punctuation">.</span></span><span class="token class-name">ActionListener</span></span><span class="token punctuation">;</span>

<span class="token doc-comment comment">/**
 * Clase que implementa la interfaz ActionListener para manejar los eventos de los botones de ataque.
 */</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">AttackButtonListener</span> <span class="token keyword">implements</span> <span class="token class-name">ActionListener</span> <span class="token punctuation">{</span>

	<span class="token comment">// Enemigo al que se le va a atacar</span>
	<span class="token keyword">private</span> <span class="token class-name">Enemy</span> enemy<span class="token punctuation">;</span>
	<span class="token keyword">private</span> <span class="token keyword">final</span> <span class="token class-name">Player</span> player<span class="token punctuation">;</span>

	<span class="token comment">// Constructor que recibe un enemigo como parámetro</span>
	<span class="token keyword">public</span> <span class="token class-name">AttackButtonListener</span><span class="token punctuation">(</span><span class="token class-name">Enemy</span> enemy<span class="token punctuation">,</span> <span class="token class-name">Player</span> player<span class="token punctuation">)</span> <span class="token punctuation">{</span>

		<span class="token keyword">this</span><span class="token punctuation">.</span>enemy <span class="token operator">=</span> enemy<span class="token punctuation">;</span>
		<span class="token keyword">this</span><span class="token punctuation">.</span>player <span class="token operator">=</span> player<span class="token punctuation">;</span>
	<span class="token punctuation">}</span>

	<span class="token comment">// Método que se ejecuta al presionar el botón de ataque</span>
	<span class="token annotation punctuation">@Override</span>
	<span class="token keyword">public</span> <span class="token keyword">void</span> <span class="token function">actionPerformed</span><span class="token punctuation">(</span><span class="token class-name">ActionEvent</span> e<span class="token punctuation">)</span> <span class="token punctuation">{</span>

		<span class="token comment">// Obtenemos las instancias de los paneles necesarios</span>
		<span class="token class-name">DialogPanel</span> dialogPanel <span class="token operator">=</span> <span class="token class-name">DialogPanel</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token comment">// Intentamos atacar al enemigo</span>
		<span class="token keyword">try</span> <span class="token punctuation">{</span>
			<span class="token comment">// Si la velocidad del jugador es mayor a la velocidad ajustada del enemigo</span>
			<span class="token keyword">if</span> <span class="token punctuation">(</span>player<span class="token punctuation">.</span><span class="token function">getSpeed</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">></span> enemy<span class="token punctuation">.</span><span class="token function">getAdjustedSpeed</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
				<span class="token comment">// Si el jugador está muerto, lanzamos una excepción</span>
				<span class="token keyword">if</span> <span class="token punctuation">(</span>player<span class="token punctuation">.</span><span class="token function">isDead</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token keyword">throw</span> <span class="token keyword">new</span> <span class="token class-name">PlayerDeadException</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
				<span class="token comment">// Si no, atacamos al enemigo</span>
				player<span class="token punctuation">.</span><span class="token function">attack</span><span class="token punctuation">(</span>enemy<span class="token punctuation">)</span><span class="token punctuation">;</span>
				<span class="token comment">// Si el enemigo no está muerto</span>
				<span class="token keyword">if</span> <span class="token punctuation">(</span><span class="token operator">!</span>enemy<span class="token punctuation">.</span><span class="token function">isDead</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
					<span class="token comment">// El enemigo nos ataca</span>
					enemy<span class="token punctuation">.</span><span class="token function">attack</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
					<span class="token comment">// Si escapa por una habilidad, para el sistema está muerto</span>
					<span class="token keyword">if</span> <span class="token punctuation">(</span>enemy<span class="token punctuation">.</span><span class="token function">isDead</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token keyword">throw</span> <span class="token keyword">new</span> <span class="token class-name">EnemyDeadException</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
				<span class="token punctuation">}</span> <span class="token keyword">else</span> <span class="token keyword">throw</span> <span class="token keyword">new</span> <span class="token class-name">EnemyDeadException</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token punctuation">}</span> <span class="token keyword">else</span> <span class="token punctuation">{</span>
				<span class="token comment">// Si el enemigo no está muerto, nos ataca</span>
				enemy<span class="token punctuation">.</span><span class="token function">attack</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
				<span class="token comment">// Si escapa por una habilidad, para el sistema está muerto</span>
				<span class="token keyword">if</span> <span class="token punctuation">(</span>enemy<span class="token punctuation">.</span><span class="token function">isDead</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token keyword">throw</span> <span class="token keyword">new</span> <span class="token class-name">EnemyDeadException</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
				<span class="token comment">// Atacamos al enemigo</span>
				player<span class="token punctuation">.</span><span class="token function">attack</span><span class="token punctuation">(</span>enemy<span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token punctuation">}</span>
			<span class="token comment">// Actualizamos los paneles</span>
			<span class="token function">updatePanels</span><span class="token punctuation">(</span>player<span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token punctuation">}</span> <span class="token keyword">catch</span> <span class="token punctuation">(</span><span class="token class-name">EnemyDeadException</span> ex<span class="token punctuation">)</span> <span class="token punctuation">{</span>
			<span class="token comment">// Si el enemigo está muerto o escapo por una habilidad</span>
			<span class="token comment">// Creamos un nuevo enemigo</span>
			enemy <span class="token operator">=</span> <span class="token class-name">EnemyFactory</span><span class="token punctuation">.</span><span class="token function">generateRegularEnemy</span><span class="token punctuation">(</span>player<span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token comment">// Asignamos el nuevo enemigo al panel de juego</span>
			<span class="token class-name">GameWindow</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">setEnemy</span><span class="token punctuation">(</span>enemy<span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token comment">// Actualizamos los paneles</span>
			<span class="token function">updatePanels</span><span class="token punctuation">(</span>player<span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token punctuation">}</span> <span class="token keyword">catch</span> <span class="token punctuation">(</span><span class="token class-name">PlayerDeadException</span> ex<span class="token punctuation">)</span> <span class="token punctuation">{</span>
			<span class="token comment">// Si el jugador está muerto, mostramos un mensaje de consolación</span>
			dialogPanel<span class="token punctuation">.</span><span class="token function">addText</span><span class="token punctuation">(</span><span class="token string">"¡Quizás deberías entrenar más duro!\n"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token comment">// Revivimos al jugador</span>
			player<span class="token punctuation">.</span><span class="token function">revive</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token comment">// Mostramos un mensaje de que el jugador fue revivido</span>
			dialogPanel<span class="token punctuation">.</span><span class="token function">addText</span><span class="token punctuation">(</span><span class="token string">"¡Has sido revivido!\n"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token comment">// Creamos un nuevo enemigo</span>
			enemy <span class="token operator">=</span> <span class="token class-name">EnemyFactory</span><span class="token punctuation">.</span><span class="token function">generateRegularEnemy</span><span class="token punctuation">(</span>player<span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token comment">// Asignamos el nuevo enemigo al panel de juego</span>
			<span class="token class-name">GameWindow</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">setEnemy</span><span class="token punctuation">(</span>enemy<span class="token punctuation">)</span><span class="token punctuation">;</span>
			<span class="token comment">// Actualizamos los paneles</span>
			<span class="token function">updatePanels</span><span class="token punctuation">(</span>player<span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token punctuation">}</span>
	<span class="token punctuation">}</span>

	<span class="token doc-comment comment">/**
	 * Método que actualiza los paneles de la interfaz gráfica.
	 *
	 * <span class="token keyword">@param</span> <span class="token parameter">player</span> Jugador actual
	 */</span>
	<span class="token keyword">private</span> <span class="token keyword">void</span> <span class="token function">updatePanels</span><span class="token punctuation">(</span><span class="token class-name">Player</span> player<span class="token punctuation">)</span> <span class="token punctuation">{</span>

		<span class="token comment">// Actualizamos los paneles</span>
		<span class="token class-name">GameWindow</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">repaint</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token class-name">StatusPanel</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span><span class="token number">0</span><span class="token punctuation">,</span> player<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">update</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token class-name">MainPanel</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span>enemy<span class="token punctuation">,</span> player<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">update</span><span class="token punctuation">(</span>enemy<span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token class-name">PlayerPanel</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span>player<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">update</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token class-name">EnemyPanel</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span>enemy<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">setEnemy</span><span class="token punctuation">(</span>enemy<span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token class-name">EnemyPanel</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span>enemy<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">update</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
		<span class="token class-name">InventoryPanel</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> player<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">update</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
	<span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-num