        <span class="token class-name">Dimension</span> <span class="token constant">SIDE_PANEL_SIZE</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Dimension</span><span class="token punctuation">(</span><span class="token number">256</span><span class="token punctuation">,</span> <span class="token number">300</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token class-name">Dimension</span> <span class="token constant">MAIN_PANEL_SIZE</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Dimension</span><span class="token punctuation">(</span><span class="token number">768</span><span class="token punctuation">,</span> <span class="token number">300</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token class-name">Dimension</span> <span class="token constant">DIALOG_PANEL_SIZE</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Dimension</span><span class="token punctuation">(</span><span class="token number">768</span><span class="token punctuation">,</span> <span class="token number">150</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token class-name">Dimension</span> <span class="token constant">DIALOG_SCROLL_SIZE</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Dimension</span><span class="token punctuation">(</span><span class="token number">748</span><span class="token punctuation">,</span> <span class="token number">140</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token class-name">Dimension</span> <span class="token constant">TAB_SIZE</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Dimension</span><span class="token punctuation">(</span><span class="token number">1275</span><span class="token punctuation">,</span> <span class="token number">340</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token class-name">Dimension</span> <span class="token constant">SPRITE_SIZE</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Dimension</span><span class="token punctuation">(</span><span class="token number">256</span><span class="token punctuation">,</span> <span class="token number">140</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
