        <span class="token class-name">Dimension</span> <span class="token constant">BUTTON_SIZE</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Dimension</span><span class="token punctuation">(</span><span class="token number">128</span><span class="token punctuation">,</span> <span class="token number">32</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
