            backgroundPanel<span class="token punctuation">.</span><span class="token function">setPreferredSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SCREEN_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            backgroundPanel<span class="token punctuation">.</span><span class="token function">setMaximumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SCREEN_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            backgroundPanel<span class="token punctuation">.</span><span class="token function">setMinimumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SCREEN_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
