            topPanel<span class="token punctuation">.</span><span class="token function">setSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">TOP_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            topPanel<span class="token punctuation">.</span><span class="token function">setPreferredSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">TOP_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            topPanel<span class="token punctuation">.</span><span class="token function">setMaximumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">TOP_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            topPanel<span class="token punctuation">.</span><span class="token function">setMinimumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">TOP_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            <span class="token class-name">System</span><span class="token punctuation">.</span>out<span class="token punctuation">.</span><span class="token function">println</span><span class="token punctuation">(</span>topPanel<span class="token punctuation">.</span><span class="token function">getSize</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            <span class="token comment">//Panel Principal</span>
            mainPanel<span class="token punctuation">.</span><span class="token function">setSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">MAIN_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            mainPanel<span class="token punctuation">.</span><span class="token function">setPreferredSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">MAIN_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            mainPanel<span class="token punctuation">.</span><span class="token function">setMaximumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">MAIN_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            mainPanel<span class="token punctuation">.</span><span class="token function">setMinimumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">MAIN_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            <span class="token comment">//Panel de Jugador</span>
            playerPanel<span class="token punctuation">.</span><span class="token function">setSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SIDE_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            playerPanel<span class="token punctuation">.</span><span class="token function">setPreferredSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SIDE_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            playerPanel<span class="token punctuation">.</span><span class="token function">setMaximumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SIDE_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            playerPanel<span class="token punctuation">.</span><span class="token function">setMinimumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SIDE_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            <span class="token comment">//Panel de Enemigo</span>
            enemyPanel<span class="token punctuation">.</span><span class="token function">setSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SIDE_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            enemyPanel<span class="token punctuation">.</span><span class="token function">setPreferredSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SIDE_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            enemyPanel<span class="token punctuation">.</span><span class="token function">setMaximumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SIDE_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
            enemyPanel<span class="token punctuation">.</span><span class="token function">setMinimumSize</span><span class="token punctuation">(</span><span class="token class-name">Dimensions</span><span class="token punctuation">.</span><span class="token constant">SIDE_PANEL_SIZE</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
