    <span class="token keyword">public</span> <span class="token keyword">abstract</span> <span class="token keyword">class</span> <span class="token class-name">ActionButton</span> <span class="token keyword">extends</span> <span class="token class-name">JButton</span> <span class="token punctuation">{</span>
    
